class Reg_user_ctrlr extends CI_Controller {
    
    function __construct() {
        parent::__construct();
        if (!$this->session->userdata('id')) {
            redirect('/', 'refresh');
        }
    }
    
	public function index()
	{
            $data['title'] = 'User Management';
            $data['company_name'] = $this->general->get_firm($this->session->userdata('firm_id'));
            $data['main_content']='registration/reg_user';
            $this->load->view('templates/standard',$data);
	}
        
        public function add()
	{
            $data = array(
                            "user_name" => $this->input->post('user_name'),
                            "password" => md5($this->input->post('password')),
                            "user_type" => $this->input->post('user_type'),
                            "created_by" => $this->session->userdata('id'),
                            "created_at" => date("Y-m-d H:i:s"),
                            "firm_id" => $this->session->userdata('firm_id')
                          );
            $this->general->save('user_reg', $data);
            redirect('registration/user');
	}
        
        public function edit_mode()
	{
            $data['title'] = 'User Management';
            $data['company_name'] = $this->general->get_firm($this->session->userdata('firm_id'));
            $data['firms']=$this->general->get_all_asc('user_reg', 'id');
            $edit_id = $this->input->post('edit_id');
            if($edit_id == '') {
                redirect('registration/user');
            }
            $data['edit_id'] = $edit_id;
            $data['edit_mode'] = $this->general->get_data_wer('user_reg', 'id', $edit_id);
            $data['main_content']='registration/reg_user';
            $this->load->view('templates/standard',$data);
	}
        
        public function edit()
	{
            $id = $this->input->post('id');
            $data = array(
                            "user_name" => $this->input->post('user_name'),
                            "password" => md5($this->input->post('password')),
                            "user_type" => $this->input->post('user_type'),
                            "updated_by" => $this->session->userdata('id'),
                            "updated_at" => date("Y-m-d H:i:s")
                          );
            $this->general->update('user_reg', $data, $id);
            redirect('registration/user');
	}
	      
}